sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var ccemails;
	//Get AMS resource object
	var createtest = new AMS_CaseFetch();
	//Check empty value for cc_emails
	if(current.u_case_cc_emails == '')
		ccemails = '';
	else
		ccemails = [current.u_case_cc_emails.toString()];
	//API call to create case
	var caseId = createtest.createCaseAndInsert(current.u_case_category.toString(), ccemails, current.u_case_details.toString(), current.u_case_type.toString(), current.u_case_service_code.toString(), current.u_incidents_priority.u_incident_priority_code.toString(), current.u_case_subject.toString());

	current.u_case_id = caseId;
	//check whether api is failed or success
	if(caseId != false){
		gs.addInfoMessage('Incident successfully created');
		current.update();
	}
	else
		{
			//if creation failed abort the action
		gs.addInfoMessage('Incident creation failed');
		current.setAbortAction(true);
	}

})(current, previous);
