sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading ||newValue === '') {
		g_form.clearValue('u_rfc_operation_description');
		return;
	}
	g_form.setValue('view','Basic','Basic');
	var time = setInterval(getType, 1000);
	var changeType = '';
	function getType() {
		changeType = g_form
				.getValue('u_rfcs_category.u_rfc_change_type_classification_change_type_id');
		var itemGA = new GlideAjax('Get_Operation_Description');
		itemGA.addParam('sysparm_name', 'getDescription');
		itemGA.addParam('sysparm_id', newValue);
		itemGA.addParam('sysparm_changeTypeId', changeType);
		itemGA.getXML(getDescription);
		function getDescription(response) {
			var description = response.responseXML.documentElement
					.getAttribute("answer");
			g_form.setValue('u_rfc_operation_description', description);
					clearInterval(time);
		}
		changeType = g_form
				.getValue('u_rfcs_category.u_rfc_change_type_classification_change_type_id');
		var params = new GlideAjax('Get_Operation_Description');
		params.addParam('sysparm_name', 'getParams');
		params.addParam('sysparm_changeTypeId', changeType);
		params.getXML(setParamFields);
		//setChangeTypeId();
		function setParamFields(response) {
			var parameters = response.responseXML.documentElement
					.getAttribute("answer");
			var parsedParams = JSON.parse(parameters);
			//var properties=parsedParams.properties;
			var fields = g_form.getEditableFields();
			var requiredList=parsedParams.required.toString();
			var requiredFields=requiredList.split(',');
			//g_form.setDisplay('u_rfcs_category.u_rfc_change_type_classification_change_type_id',true);
			for(var count=0;count<=requiredFields.length;count++) {
				g_form.elements.forEach(function (e, i) {
					var field = e.fieldName.replace(/u_rfcs/g, "");
					field = field.replace(/_/g, "");
					if (field == requiredFields[count].toLowerCase()) {
						g_form.setVisible(e.fieldName,true);
						g_form.setMandatory(e.fieldName, true);
					}
					else{				
						g_form.setDisplay(e.fieldName,false);
						g_form.setDisplay('u_rfcs_category',true);
						if(newValue=='Deployment'){
						g_form.setDisplay('u_rfcs_description',true);
						g_form.setMandatory('u_rfcs_description', true);
						}else{
							g_form.setSectionDisplay('resource_information',false);	
						}
						g_form.setDisplay('u_rfcs_start_time',true);
						g_form.setDisplay('view',true);	
						g_form.setDisplay('u_rfcs_category.u_rfc_change_type_classification_subcategory',true);
						g_form.setDisplay('u_rfcs_category.u_rfc_change_type_classification_item',true);
						g_form.setDisplay('u_rfc_operation_description',true);
					g_form.setDisplay('u_rfcs_category.u_rfc_change_type_classification_operation',true);
					}
				});
 				setChangeTypeId(changeType);
			}
		}
	}
}
