sys_script_include.script:
var AMSGetResources = Class.create();
AMSGetResources.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
getVpcs: function() {
		var gr = new GlideRecord('x_109369_ams_vpcs');
		gr.query();
		var map=[];
		while(gr.next()){

			//map=map+gr.u_vpc_id+",";
			map.push(gr.u_vpc_id.toString());

		}
		return map.toString();
	},
	getStacks: function() {
		var gr = new GlideRecord('x_109369_ams_stacks');
		gr.query();
		var map=[];
		while(gr.next()){
			//map=map+gr.u_stack_id+",";
			map.push(gr.u_stack_id.toString());

		}
		return map.toString();
	},
	getAmis: function() {
		var gr = new GlideRecord('x_109369_ams_amis');
		gr.query();
		var map=[];
		while(gr.next()){
			//map=map+gr.u_ami_id+",";
			map.push(gr.u_ami_id.toString());

		}
		return map.toString();
	},
	getSubnets: function() {
		var gr = new GlideRecord('x_109369_ams_subnets');
		gr.query();
		var map=[];
		while(gr.next()){
			//map=map+gr.u_subnet_id+",";		
			map.push(gr.u_subnet_id.toString());
		}

		return map.toString();
	},

	type: 'AMSGetResources'
});
