sys_script_include.script:
var Get_Operation_Description = Class.create();
Get_Operation_Description.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getDescription:function(){
		var categoryId=this.getParameter('sysparm_id');
		var value='';
		var itemGR = new GlideRecord('x_109369_ams_rfc_change_type_classification');
		itemGR.addQuery('sys_id',categoryId);
		itemGR.query();
		if(itemGR.next()){
			var changeIDGR=new GlideRecord('x_109369_ams_rfc_change_type_version');
			changeIDGR.addQuery('u_rfc_change_type_version_changetypeid',itemGR.u_rfc_change_type_classification_change_type_id);
			changeIDGR.query();
			if(changeIDGR.next()){
				value="Change Type Id:\n" + changeIDGR.u_rfc_change_type_version_changetypeid+"\n\n"+"Description: \n"+ changeIDGR.u_rfc_change_type_version_description+"\n\n";
				if(changeIDGR.u_rfc_change_type_version_version!=undefined){
					value+="Change Type Version: \n"+changeIDGR.u_rfc_change_type_version_version+" \n\n";
				}
				if(changeIDGR.u_rfc_change_type_version_automationstatus!=undefined){value+="Execution Mode:"+"\n"+changeIDGR.u_rfc_change_type_version_automationstatus.getDisplayValue()+"\n\n";
				}
				value+="AWS Approval Required";
			}else{
				value="Change Type Id:\n" +this.getParameter('sysparm_changeTypeId')+"\n\n"+"Description:\n"+
				"You do not have permission to create an RFC with this CTI";}
			}
			return value;
		},
		getParams:function()
		{
			var changeTypeId=this.getParameter('sysparm_changeTypeId');
			var showFields={};
				var params = new GlideRecord('x_109369_ams_rfc_change_type_version');
				params.addQuery('u_rfc_change_type_version_changetypeid',changeTypeId);
				params.query();
				if(params.next()){
					showFields["required"]=params.u_rfc_change_type_version_required.toString();
					var fields=JSON.parse(params.u_rfc_change_type_version_parameters);
					var exeMap={};
				Object.keys(fields).forEach(function(key) {
					if (fields[key].properties != undefined) {
						var paramMap = fields[key].properties;
						Object.keys(paramMap).forEach(function(propertyKey) {
							Object.keys(paramMap[propertyKey]).forEach(function(propertyInsideKey) {
								if (propertyInsideKey == 'default') {
									exeMap['u_rfcs_'+propertyKey.toLowerCase()] = paramMap[propertyKey][propertyInsideKey];
								} else {
									exeMap[propertyKey] = "";
								}
							});
						});
					} else {
						exeMap[key] = "";
					}
				});
					showFields["properties"]=exeMap;
				}
				return JSON.stringify(showFields);
			},
			type: 'Get_Operation_Description'
		});
